import React, { Component } from 'react';
import { postsFetched } from "./config/actions/index";
import { connect } from "react-redux";
import {fire  , base}  from './config/Fire';

export class Edit extends Component {
  constructor(props) {
    super(props);
    this.add = this.add.bind(this);
   
   // this.handleChange = this.handleChange.bind(this);
    this.state = {
        posts: {},
        currentUser: null,
        title: 'sss',
        text: '',
        color:''
      };
}
componentWillMount(){
  this.postsRef = base.syncState('posts',{
      context: this,
      state: 'posts'
  });

  
}
handleChangeTitle(e){
  e.preventDefault();

  const title = e.target.value;
  this.setState({title});


  console.log(this.state.title);

}
handleChangeText(e){
  e.preventDefault();
  const text = e.target.value;
 
  

  this.setState({text});
  console.log(e.target.value);
 

}
handleChangeColor(e){
  e.preventDefault();
  const color = e.target.value;
 
  

  this.setState({color});
  console.log(e.target.value);
 

}
update(e){
  e.preventDefault();
  
  const title = this.state.title;
  const text = this.state.text;
  const color = this.state.color;

  base.bindCollection('posts').oc(this.props.location.id).update({
    title: title,
    text: text,
    color: color
  })
  
 
  //fire.database().ref().child('posts').child('1').set(data);
  console.log(this.props.posts);
}
componentDidUpdate(){
  console.log(this.props.posts);
 }
 
  render() {
      //wyśietlanie listy projektów
     
      console.log(this.props.location.title);

      return (<div>
        <h1>Edytuj</h1>
        <form>
        <div className="form-group">
         <label htmlFor="title">Tytuł</label>
         <input type="textarea"  value={this.props.location.title} onChange={this.handleChangeTitle.bind(this)} name="title" className="form-control" placeholder="Enter title" />
         <small id="emailHelp" className="form-text ">We'll never share your email with anyone else.</small>
        </div>
         <div className="form-group">
        <label htmlFor="exampleInputPassword1">treść</label>
        <input value={this.props.location.text} name="name" onChange={this.handleChangeText.bind(this)} type="textarea" className="form-control" id="exampleInputPassword1" placeholder="Text" />
      <input name="color" value={this.props.location.color} type="color" id="myColor" onChange={this.handleChangeColor.bind(this)} ></input>
        </div>
        <button type="submit" onClick={this.login} className="btn btn-primary">Usuń</button>
        <button onClick={this.update} style={{marginLeft: '25px'}} className="btn btn-success">Zmień</button>
   </form>
   </div>
      )
  }
}

const mapStateToProps = (state) => {
  
  return {
    
    posts: state.posts
  }
};
const mapDispatchToProps = { postsFetched };

export const EditContainer = connect(mapStateToProps, mapDispatchToProps)(Edit);
