import React, { Component } from 'react';
import {fire  , base}  from './config/Fire';
import {ListContainer} from './list';

class Backend extends Component {
    constructor(props) {
        super(props);
        this.add = this.add.bind(this);
        this.update = this.update.bind(this);
        this.logout = this.logout.bind(this);
       // this.handleChange = this.handleChange.bind(this);
        this.state = {
            posts: {},
            currentUser: null,
            title: 'sss',
            text: ''
          };
    }
    handleChangeTitle(e){
        e.preventDefault();
    
        const title = e.target.value;
        this.setState({title});
    
      
        console.log(this.state.title);

    }
    handleChangeText(e){
        e.preventDefault();
        const text = e.target.value;
       
        
    
        this.setState({text});
        console.log(e.target.value);
       

    }
    componentWillMount(){
        this.postsRef = base.syncState('posts',{
            context: this,
            state: 'posts'
        });
      
        
    }
    componentWillUnmount(){
        base.removeBinding(this.postsRef);
    }
    update(post){
        const posts  ={...this.state.posts};
        posts[post.id]= post;
        this.setState({posts});
    }


    logout() {
        fire.auth().signOut();
    }
    add(e){
        e.preventDefault();
        const posts = {...this.state.posts};
        const id = Date.now();
        const title = this.state.title;
        const text = this.state.text;
        console.log(id);
        posts[id] = {
          title: title,
         text: text,
         // owner: this.state.currentUser.uid
        };
        console.log( posts);
        
        this.setState({posts});
        var data={
            "title":title,
            "text":text
        };
        //fire.database().ref().child('posts').child('1').set(data);
        console.log(this.props.posts);
      }

    render() {
        return (<div className="container"> 
            <h1>Welcome to Backend!</h1>
            <div className="container">
            <div className="row justify-content-md-center">
            <div className="col-md-6"><ListContainer /></div>
       <div className="col-md-6 ">
       <form>
      <div className="form-group">
       <label htmlFor="title">Tytuł</label>
       <input type="textarea"  value={this.state.title} onChange={this.handleChangeTitle.bind(this)} name="title" className="form-control" placeholder="Enter title" />
       <small id="emailHelp" className="form-text ">We'll never share your email with anyone else.</small>
      </div>
       <div className="form-group">
      <label htmlFor="exampleInputPassword1">treść</label>
      <input value={this.state.text} name="name" onChange={this.handleChangeText.bind(this)} type="textarea" className="form-control" id="exampleInputPassword1" placeholder="Text" />
      
      </div>
      <button type="submit" onClick={this.login} className="btn btn-primary">Login</button>
      <button onClick={this.add} style={{marginLeft: '25px'}} className="btn btn-success">dodaj{this.state.title}</button>
 </form>
 </div>
 </div>
 </div>
 
            <button className="btn btn-success" onClick={this.logout}>Wyloguj</button>
        </div>)

    }

}

export default Backend;
