        posts: {},
        currentUser: null,
        title: this.props.location.title,
        text: this.props.location.text,
        color:  this.props.location.color
      };
}
componentWillMount(){
  this.postsRef = base.syncState('posts',{
      context: this,
      state: 'posts'
  });
  
  
}
