  e.preventDefault();
  const color = e.target.value;
 
  

  this.setState({color});
  console.log(e.target.value);
 

}
update(e){
  e.preventDefault();
  const id = this.state.id;
  const title = this.state.title;
  const text = this.state.text;
  const color = this.state.color;
