import React, { Component } from 'react';
import {fire  , base}  from './config/Fire';
import {ListContainer} from './list';

class Backend extends Component {
    constructor(props) {
        super(props);
        this.add = this.add.bind(this);
        this.update = this.update.bind(this);
        this.logout = this.logout.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            posts: {},
            currentUser: null,
            title: '',
            text: ''
          };
    }
    handleChange(e){
        e.preventDefault();
        const text = e.target.text;
        const title = e.target.title;
        this.setState({title});
    
        this.setState({text});
        //console.log(this.state.posts);

    }
    componentWillMount(){
        this.postsRef = base.syncState('posts',{
            context: this,
            state: 'posts'
        });
      
        
    }
    componentWillUnmount(){
        base.removeBinding(this.postsRef);
    }
    update(post){
        const posts  ={...this.state.posts};
        posts[post.id]= post;
        this.setState({posts});
    }


    logout() {
        fire.auth().signOut();
    }
    add(e){
        e.preventDefault();
        const posts = {...this.state.posts};
        const id = Date.now();
        posts[id] = {
          id: id,
          title: "ww",
         
          text: e.text,
          chordpro: "",
         // owner: this.state.currentUser.uid
        };
        console.log(this.state.text);
    
        //this.setState({posts});
      }

    render() {
        return (<div className="container"> 
            <h1>Welcome to Backend!</h1>
            <div className="container">
            <div className="row justify-content-md-center">
            <div className="col-md-6"><ListContainer /></div>
       <div className="col-md-6 ">
       <form>
      <div className="form-group">
       <label htmlFor="title">Tytuł</label>
       <input type="textarea"  value={this.state.title} onChange={this.handleChange} name="title" className="form-control" placeholder="Enter title" />
       <small id="emailHelp" className="form-text ">We'll never share your email with anyone else.</small>
      </div>
       <div className="form-group">
      <label htmlFor="exampleInputPassword1">treść</label>
      <input value={this.state.text} name="name" onChange={this.handleChange} type="textarea" className="form-control" id="exampleInputPassword1" placeholder="Text" />
      <input type="text"></input>
      </div>
      <button type="submit" onClick={this.login} className="btn btn-primary">Login</button>
      <button onClick={this.add} style={{marginLeft: '25px'}} className="btn btn-success">dodaj</button>
 </form>
 </div>
 </div>
 </div>
 
            <button className="btn btn-success" onClick={this.logout}>Wyloguj</button>
        </div>)

    }

}

export default Backend;
