  constructor(props) {
    super(props);
    this.sendState = this.sendState.bind(this);
    this.onSync = this.onSync.bind(this);
    this.state = {
      articles: [
        { title: "React Redux Tutorial for Beginners", id: 1 },
        { title: "TypeScript tutorial for beginners", id: 2 }
      ],
      temporary: []
    };
  }
  async componentDidMount() {
