import * as React from "react";
import logo from './logo.svg';
import './App.css';
import {
  BrowserRouter as Router,
  Route,
  Link
} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Projects from './projects';
import About from './about';
import Chat from './Chat';
import Admin from './Admin';
import { connect } from "react-redux";
import { postsFetched } from "./config/actions/index";
import {fire  , base}  from './config/Fire';

export class App extends React.Component {
  constructor(props) {
    super(props);
    this.sendState = this.sendState.bind(this);
    this.state = {
      articles: [
        { title: "React Redux Tutorial for Beginners", id: 1 },
        { title: "TypeScript tutorial for beginners", id: 2 }
      ],
      temporary: []
    };
  }
  componentDidMount() {
    // fetch("https://randomuser.me/api/?format=json&results=10")
    //   .then(res => res.json())
    //   .then(json => this.props.postsFetched(json.results));
      this.postsRef = base.syncState('posts',{
        context: this,
        state: 'temporary'
    });
    this.props.postsFetched(this.state.temporary);
    this.sendState();


  }
  sendState(){
    console.log(this.state.temporary);
  }
  componentDidUpdate(){

   // console.log(this.props.posts);
  }
  render() {
  return (
    <div className="App">
      <Router>

      <header className="App-header">
      <ul>
            <li>
              <Link to="/projects">Projects</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/chat">Chat</Link>
            </li>
          </ul>


      </header>
      <Route exact path="/projects" component={Projects} />
      <Route path="/about" component={About} />
      <Route path="/chat" component={Chat} />
      <Route path="/admin" component={Admin} />
      </Router>
    </div>
  );
  }
}
const mapStateToProps = (state) => {

  return {

    posts: state.posts
  }
};
const mapDispatchToProps = { postsFetched };

export const AppContainer = connect(mapStateToProps, mapDispatchToProps)(App);
