            <div className="row justify-content-md-center">
       <div className="col-md-12 " style={{height: 200}}>
       <h1>{this.props.title}</h1>
    <p style={{Color: 'lightblue'}}>{this.props.text}</p>
    <p style={{Color: 'lightblue'}}>{this.props.id}sss</p>
    
